import axios from 'axios';
import React, { useCallback, useEffect, useState } from 'react';
import { AiOutlineSearch } from 'react-icons/ai';
import { Card, Input } from 'semantic-ui-react';
import Image from '../../assets/point.jpg';
import './HeroStyles.css';

function Hero() {
  const [searchTerm, setSearchTerm] = useState('');
  const [bookData, setBookData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    fetchBookData();
  }, []);

  const fetchBookData = async () => {
    try {
      const response = await axios.get('http://localhost:8080/book');
      setBookData(response.data);
      setFilteredData(response.data);
    } catch (error) {
      console.error('Error fetching book data:', error);
    }
  };

  const handleSearch = useCallback(
    (searchValue) => {
      setSearchTerm(searchValue);
      const filteredData = bookData.filter((item) => {
        return (
          item.title.toLowerCase().includes(searchValue.toLowerCase()) ||
          item.author.toLowerCase().includes(searchValue.toLowerCase())
        );
      });
      setFilteredData(filteredData);
    },
    [bookData],
  );

  useEffect(() => {
    if (searchTerm === '') {
      setFilteredData(bookData);
    } else {
      handleSearch(searchTerm);
    }
  }, [searchTerm, handleSearch, bookData]);

  return (
    <div className="hero">
      <img src={Image} id="image" alt="BigCo Inc. logo" />
      <div className="overlay"></div>
      <div className="content">
        <form className="form" onSubmit={handleSearch}>
          <Input
            icon="search"
            placeholder="화성시 맛집 검색"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button type="submit">
            <AiOutlineSearch className="icon" />
          </button>
        </form>
      </div>
      <Card.Group itemsPerRow={3} style={{ insetBlockStart: 20 }}>
        {filteredData.map((item) => (
          <Card key={item.id}>
            <Card.Content>
              <Card.Header>{item.title}</Card.Header>
              <Card.Description>{item.author}</Card.Description>
            </Card.Content>
          </Card>
        ))}
      </Card.Group>
    </div>
  );
}

export default Hero;
